import React, { useEffect, useState } from "react";
import {
    MainContainer, ScrollableContainer, HeaderBar, StyledDrawer, NavButton,
    ResponsiveGrid, FeatureCard, CardIcon
} from "../styles/homeStyles";
import { AppBar, Toolbar, Typography, Box, IconButton, List, ListItem, ListItemIcon, ListItemText, CircularProgress } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import LogoutIcon from "@mui/icons-material/Logout";
import PeopleIcon from "@mui/icons-material/People"; // Icon for Users
import { useNavigate } from "react-router-dom";

function User() {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedUser, setSelectedUser] = useState(null);
    const [showUpdateOverlay, setShowUpdateOverlay] = useState(false);
    const [updatedUser, setUpdatedUser] = useState({});
    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch("http://localhost:8080/api/admin/users", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            }
        })
            .then(res => {
                if (!res.ok) {
                    throw new Error(`HTTP error! Status: ${res.status}`);
                }
                return res.json();
            })
            .then(data => {
                setUsers(data);
                setLoading(false);
            })
            .catch(err => {
                console.error("Error fetching users:", err);
                setError(err.message || "An unexpected error occurred.");
                setLoading(false);
            });
    };

    const handleSelectUser = (user) => {
        setSelectedUser(user);
    };

    const handleDeleteUser = () => {
        if (!selectedUser) return;
        const confirmDelete = window.confirm(`Are you sure you want to delete ${selectedUser.firstName} ${selectedUser.lastName}?`);
        if (!confirmDelete) return;

        fetch(`http://localhost:8080/api/admin/users/${selectedUser.email}`, {
            method: "DELETE",
            headers: {
                "Authorization": `Bearer ${token}`
            }
        })
            .then(response => {
                if (!response.ok) throw new Error("Failed to delete user.");
                alert(`${selectedUser.firstName} has been deleted.`);
                setSelectedUser(null);
                fetchUsers();
            })
            .catch(err => console.error("Error deleting user:", err));
    };

    const handleUpdateUser = () => {
        if (!selectedUser) return;
        setUpdatedUser({ ...selectedUser });
        setShowUpdateOverlay(true);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedUser(prevState => ({ ...prevState, [name]: value }));
    };

    const handleUpdateSubmit = () => {
        fetch(`http://localhost:8080/api/admin/users/${updatedUser.email}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            },
            body: JSON.stringify(updatedUser)
        })
            .then(res => {
                if (!res.ok) throw new Error("Failed to update user.");
                alert(`${updatedUser.firstName} has been updated.`);
                setShowUpdateOverlay(false);
                fetchUsers();
            })
            .catch(err => console.error("Error updating user:", err));
    };

    const handleDrawerToggle = () => {
        setSelectedUser(null);
    };

    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/login");
    };

    const drawer = (
        <Box sx={{ height: "100%", display: "flex", flexDirection: "column", overflow: "hidden" }}>
            <Box sx={{ p: 2, display: "flex", alignItems: "center", justifyContent: "center" }}>
                <Typography variant="h6" sx={{ color: "white", fontWeight: 600 }}>
                    Admin Panel
                </Typography>
            </Box>
            <List>
                <ListItem disablePadding>
                    <NavButton active onClick={() => navigate("/manage-users")} sx={{ width: "100%" }}>
                        <ListItemIcon sx={{ color: "white", minWidth: 40 }}>
                            <PeopleIcon />
                        </ListItemIcon>
                        <ListItemText primary="Manage Users" />
                    </NavButton>
                </ListItem>
                <ListItem disablePadding>
                    <NavButton onClick={handleLogout} sx={{ width: "100%" }}>
                        <ListItemIcon sx={{ color: "white", minWidth: 40 }}>
                            <LogoutIcon />
                        </ListItemIcon>
                        <ListItemText primary="Logout" />
                    </NavButton>
                </ListItem>
            </List>
        </Box>
    );

    return (
        <MainContainer>
            <StyledDrawer variant="permanent">{drawer}</StyledDrawer>

            <Box component="main" sx={{ flexGrow: 1, height: "100vh", overflow: "hidden", display: "flex", flexDirection: "column" }}>
                <HeaderBar position="static">
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            edge="start"
                            onClick={handleDrawerToggle}
                            sx={{
                                mr: 2,
                                display: { sm: "none" },
                                color: "white"
                            }}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h5" sx={{
                            color: "white",
                            fontWeight: 500,
                            letterSpacing: "0.5px",
                            flexGrow: 1
                        }}>
                            User Management
                        </Typography>
                    </Toolbar>
                </HeaderBar>

                <ScrollableContainer sx={{ p: 3, flexGrow: 1 }}>
                    <ResponsiveGrid>
                        {users.length > 0 ? (
                            users.map(user => (
                                <FeatureCard
                                    key={user.id}
                                    active
                                    onClick={() => handleSelectUser(user)}
                                    sx={{ cursor: "pointer", border: selectedUser?.id === user.id ? "2px solid #6200aa" : "none" }}
                                >
                                    <CardIcon active>
                                        <PeopleIcon />
                                    </CardIcon>
                                    <Typography variant="h6">{user.firstName} {user.lastName}</Typography>
                                    <Typography variant="body2" color="text.secondary"><strong>Email:</strong> {user.email}</Typography>
                                    <Typography variant="body2" color="text.secondary"><strong>Role:</strong> {user.role}</Typography>
                                </FeatureCard>
                            ))
                        ) : (
                            <Typography variant="h6" sx={{ textAlign: "center", color: "gray" }}>
                                No users found.
                            </Typography>
                        )}
                    </ResponsiveGrid>
                </ScrollableContainer>

                {selectedUser && (
                    <Box sx={{ p: 3, textAlign: "center" }}>
                        <Button variant="contained" color="primary" startIcon={<EditIcon />} onClick={handleUpdateUser}>Edit</Button>
                        <Button variant="contained" color="error" startIcon={<DeleteIcon />} onClick={handleDeleteUser} sx={{ ml: 2 }}>Delete</Button>
                    </Box>
                )}
            </Box>

            {showUpdateOverlay && (
                <Box sx={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", bgcolor: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center" }}>
                    <Box sx={{ bgcolor: "white", p: 3, borderRadius: "10px", width: "400px" }}>
                        <Typography variant="h6">Update User</Typography>
                        <TextField fullWidth label="First Name" name="firstName" value={updatedUser.firstName} onChange={handleInputChange} sx={{ my: 1 }} />
                        <Button fullWidth variant="contained" color="primary" onClick={handleUpdateSubmit}>Save</Button>
                    </Box>
                </Box>
            )}
        </MainContainer>
    );
}

export default User;
