import React, { useEffect, useState } from "react";

function User() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [error, setError] = useState(null);
    const [showUpdateOverlay, setShowUpdateOverlay] = useState(false);
    const [updatedUser, setUpdatedUser] = useState({});

    const token = localStorage.getItem("token");

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch("http://localhost:8080/api/admin/users", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            },
            credentials: "include"
        })
            .then(res => {
                if (!res.ok) {
                    throw new Error(`HTTP error! Status: ${res.status}`);
                }
                return res.json();
            })
            .then(data => {
                console.log("Fetched users:", data);
                setUsers(data);
            })
            .catch(err => {
                console.error("Error fetching users:", err);
                setError(err.message || "An unexpected error occurred.");
            });
    };

    // Select user
    const handleSelectUser = (user) => {
        setSelectedUser(user);
    };

    // Confirm and delete user
    const handleDeleteUser = () => {
        if (!selectedUser) return;
        const confirmDelete = window.confirm(`Are you sure you want to delete ${selectedUser.username}?`);
        if (!confirmDelete) return;

        fetch(`http://localhost:8080/api/admin/users/${selectedUser.email}`, {
            method: "DELETE",
            headers: {
                "Authorization": `Bearer ${token}`
            }
        })
            .then(response => {
                if (!response.ok) throw new Error("Failed to delete user.");
                alert(`${selectedUser.username} has been deleted.`);
                setSelectedUser(null);
                fetchUsers();
            })
            .catch(err => console.error("Error deleting user:", err));
    };

    // Open update overlay
    const handleUpdateUser = () => {
        if (!selectedUser) return;
        setUpdatedUser({ ...selectedUser });
        setShowUpdateOverlay(true);
    };

    // Handle update form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedUser(prevState => ({ ...prevState, [name]: value }));
    };

    // Submit updated user details
    const handleUpdateSubmit = () => {
        fetch(`http://localhost:8080/api/admin/users/${updatedUser.email}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            },
            body: JSON.stringify(updatedUser)
        })
            .then(res => {
                if (!res.ok) throw new Error("Failed to update user.");
                alert(`${updatedUser.username} has been updated.`);
                setShowUpdateOverlay(false);
                fetchUsers();
            })
            .catch(err => console.error("Error updating user:", err));
    };

    return (
        <div className="user-management-container">
            {error && <div className="error-message">{error}</div>}
            <h2>User Management</h2>

            <div className="user-list">
                {users.length > 0 ? (
                    users.map(user => (
                        <div
                            key={user.id}
                            className={`user-card ${selectedUser?.id === user.id ? "selected" : ""}`}
                            onClick={() => handleSelectUser(user)}
                        >
                            <div className="user-info">
                                <span className="user-fName">{user.firstName}</span>
                                <span className="user-sName">{user.lastName}</span>
                                <span className="user-email">{user.email}</span>
                                <span className="user-role">{user.role}</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No users found.</p>
                )}
            </div>

            {selectedUser && (
                <div className="user-actions">
                    <button className="action-btn edit" onClick={handleUpdateUser}>Edit</button>
                    <button className="action-btn delete" onClick={handleDeleteUser}>Delete</button>
                </div>
            )}

            {/* Update User Overlay */}
            {showUpdateOverlay && (
                <div className="update-overlay">
                    <div className="update-form">
                        <h3>Update {updatedUser.username}'s Details</h3>
                        <label>
                            First Name:
                            <input type="text" name="firstName" value={updatedUser.firstName} onChange={handleInputChange} />
                        </label>
                        <label>
                            Last Name:
                            <input type="text" name="lastName" value={updatedUser.lastName} onChange={handleInputChange} />
                        </label>
                        <label>
                            Email:
                            <input type="email" name="email" value={updatedUser.email} onChange={handleInputChange} />
                        </label>
                        <label>
                            Role:
                            <input type="text" name="role" value={updatedUser.role} onChange={handleInputChange} />
                        </label>
                        <button onClick={handleUpdateSubmit}>Save</button>
                        <button onClick={() => setShowUpdateOverlay(false)}>Cancel</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default User;
